import styled from 'styled-components';
import { theme } from 'assets/styles/theme';

export const StyledAverage = styled.div`
  width: 34px;
  height: 34px;
  border-radius: 50%;
  border: none;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${({ num }) =>
    num < 2 ? theme.colors.error : num > 2 && num < 4 ? theme.colors.warning : num > 4 ? theme.colors.success : 'black'};
`;
